import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import albumData from './../data/albums';



class Library extends Component {
 constructor(props){
  super(props);
  this.state = {albums: albumData}


 }


 render(){
  return(
   <section className='library'>
    <h1 id="librayBranding">Latest Albums</h1>
     <div className="flex-container">
   {
     this.state.albums.map((album, index) =>
     <Link className="flex-albums" to={`/album/${album.slug}`} key={index}>

     <div class="containerAlbum">
        <img className="albumCover"src={album.albumCover} alt={album.title}/>
        <div class="overlay">
     		<div className="albumInfo">
         	     <h1>{album.title}</h1>
         	     <h2>{album.artist}</h2>
          	   <div>{album.songs.length} songs</div>
              </div>
       </div>
     </div>

     </Link>
    )
   }
    </div>
    <div className="extra-space"></div>
    <footer>
      <p>CHAU.Z, Copyright Â© 2019</p>
    </footer>
   </section>
  );
 }
}

export default Library;
